import React, {useState} from 'react';

import MoviesList from './components/MoviesList';
import './App.css';
import Axios from 'axios';
function App() {
  const [movies, setMovies]= useState([]);
  const [isLoading, setIsLoading] = useState(false);

  // const showHandler = () => {
  //   if(movies.length>0){
  //     setShowMoviesList(true)
  //   }
  // }
   async function fetchMoviesHandler(){
     const response = await fetch('https://swapi.dev/api/films/');
     const data = await response.json();

      const transformedMovies = data.results.map(result => {
        return {
          id: result.episode_id,
          title:result.title,
          openingText: result.opening_crawl,
          releaseData: result.release_date
        }

      });
        setMovies(transformedMovies);
        setIsLoading(false);
    // Axios.get('https://swapi.dev/api/films/').then(resp  => {
    //     const movieDatas = resp.data.results;
    //     const transformedMovies = movieDatas.map(movieData => {
    //       return {
    //         id: movieData.id,
    //         title:movieData.title,
    //         openingText: movieData.opening_crawl,
    //         releaseData: movieData.release_date
    //       }
    //     });
    //     setMovies(transformedMovies);
    // });
  }


  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
       {!isLoading && <MoviesList movies={movies} />}
      </section>
    </React.Fragment>
  );
}

export default App;
