import React, {useState} from 'react';

import MoviesList from './components/MoviesList';
import './App.css';
import Axios from 'axios';
function App() {
  const [movies, setMovies]= useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const[error, setError] = useState(null);

  // const showHandler = () => {
  //   if(movies.length>0){
  //     setShowMoviesList(true)
  //   }
  // }
   async function fetchMoviesHandler(){
     setIsLoading(true);
     setError(null);
     try {
      const response = await fetch('https://swapi.dev/api/films/');
      if(!response.ok){
        throw new Error('Something Went Wrong Here')
      }
      const data = await response.json();
       const transformedMovies = data.results.map(result => {
         return {
           id: result.episode_id,
           title:result.title,
           openingText: result.opening_crawl,
           releaseData: result.release_date
         }

       });
         setMovies(transformedMovies);
        } catch(error){
          setError(error.message);
        }
        setIsLoading(false);

    // Axios.get('https://swapi.dev/api/films/').then(resp  => {
    //     const movieDatas = resp.data.results;
    //     const transformedMovies = movieDatas.map(movieData => {
    //       return {
    //         id: movieData.id,
    //         title:movieData.title,
    //         openingText: movieData.opening_crawl,
    //         releaseData: movieData.release_date
    //       }
    //     });
    //     setMovies(transformedMovies);
    // });
  }


  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
    <section>
       {!isLoading && movies.length > 0 && <MoviesList movies={movies} />}
       {!isLoading && !error && movies.length === 0 && <p>Found No movies</p> }
       {isLoading && <p>Loading......</p>}
       {!isLoading && error && <p>{error}</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
